// web专用mixin，在web中实现页面 backgroundColor 功能
export default function pageBackgroundMixin (mixinType) {
  if (mixinType === 'page' && __mpx_mode__ === 'web') {
    return {
      activated () {
        console.log(this.$vnode.componentOptions.Ctor.options)
        if (this.$vnode.componentOptions) {
          document.body.style.backgroundColor = this.$vnode.componentOptions.Ctor.options.pageBackgroundColor || ''
        }
      }
    }
  }
}
